mapper.select:
SELECT ROUND(AVG(REVIEWGRADE), 1) AS AVERAGE_GRADE
FROM TRAVELREVIEW
WHERE CONTENTID = #{contentId}

SELECT COALESCE(COUNT(*), 0) FROM TRAVELREVIEW WHERE CONTENTID = COALESCE(#{contentid}, 0)
AND EMAIL = #{email}
AND EMAIL IS NULL

UPDATE TravelList
SET TRAVELVIEWCOUNT = COALESCE(TRAVELVIEWCOUNT, 0) + 1
WHERE contentId = #{contentId}

mapper.update:
UPDATE TRAVELLIST
SET overview = #{overview}
WHERE contentid = #{contentid}

UPDATE TravelList
SET LIKECOUNT = COALESCE(LIKECOUNT, 0) + 1
WHERE contentId = #{contentId}

mapper.select:
SELECT CAT1, CAT2, CAT3, CAT1KR, CAT2KR, CAT3KR 
FROM TOURRISMSERVICE
WHERE cat1 = #{cat1} and cat2 = #{cat2} and cat3 = #{cat3}

mapper.update:
UPDATE TRAVELREVIEW
SET reviewcomment = #{reviewcomment}, REVIEWDATE = SYSDATE
WHERE reviewno = #{reviewno}
