});


// 별점 기능을 위한 스크립트
const stars = document.querySelectorAll('.star');
stars.forEach((star, index) => {
    star.addEventListener('click', () => {
        stars.forEach((otherStar, otherIndex) => {
            if (otherIndex <= index) {
                otherStar.textContent = '★'; // 별점을 채움
            } else {
                otherStar.textContent = '☆'; // 별점을 비움
            }
        });
    });
});

//리뷰 보여주는 스크립트.
const reviews = [
    { profilePic: "https://w1.pngwing.com/pngs/860/49/png-transparent-circle-silhouette-user-user-interface-user-profile-black-black-and-white-line-thumbnail.png", content: "와 벛꽃 축제 가고싶네" },
    // 다른 리뷰들...
];

// 리뷰를 보여주는 함수
function displayReviews() {
    const reviewDisplay = document.getElementById("review-display-placeholder");

    // 기존의 리뷰 내용을 초기화
    reviewDisplay.innerHTML = "";

    // 각 리뷰를 순회하면서 화면에 추가
    reviews.forEach(review => {
        const reviewElement = document.createElement("div");
        reviewElement.classList.add("user-review");
        reviewElement.innerHTML = `
            <img src="${review.profilePic}" class="user-profile-pic" alt="프로필 사진">
            <div class="user-review-content">
                <p>${review.content}</p>
            </div>
            <div class="user-review-buttons">
                <button>좋아요</button>
                <button>답글</button>
            </div>
        `;
        reviewDisplay.appendChild(reviewElement);
    });
}

// 페이지 로드 시 리뷰를 표시
window.onload = displayReviews;

//한국관광공사 api 테스트
document.addEventListener("DOMContentLoaded", function() {
    fetchTravelInfo();
});

async function fetchTravelInfo() {
    const apiKey = "eTvi0rTQ1PoHjUzFGNoNUjpVx%2BMk6y8Hs%2FyH4JzAlRk5Ag7c5rqIcBWoLWuG%2BJoHzywuB1cVkEHiZZFuhDYbhA%3D%3D"; // 실제 API 키로 교체해야 합니다.
    const url = `https://apis.data.go.kr/B551011/KorService1/locationBasedList1?serviceKey=${apiKey}&numOfRows=10&pageNo=1&MobileOS=ETC&MobileApp=AppTest&_type=json&listYN=Y&arrange=A&mapX=126.981611&mapY=37.568477&radius=1000&contentTypeId=15`;

    try {
        const response = await fetch(url);
        const data1 = await response.json();      
            displayTravelInfo(data1.response.body.items.item);
        
    } catch (error) {
        console.error("API 호출 중 오류 발생:", error);
    }
}

function displayTravelInfo(info) {
    // 여행지 이름을 표시할 요소
    const namePlaceholder = document.getElementById("name-placeholder");
    namePlaceholder.textContent = `여행지 이름: ${info[0].title}`;
    console.log(info)

    // 여행지 위치를 표시할 요소
    const mapPlaceholder = document.getElementById("map-placeholder");
    mapPlaceholder.textContent = `위치: ${info[0].addr1}`;
}
